          <p className="text-gray-600">„Éá„Éº„Çø„ÇíË™≠„ÅøËæº„Çì„Åß„ÅÑ„Åæ„Åô...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">
            GitHub Repositories Dashboard
          </h1>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
            <div className="bg-blue-50 p-4 rounded-lg">
              <div className="text-sm text-blue-600 font-medium">Á∑è„É™„Éù„Ç∏„Éà„É™Êï∞</div>
              <div className="text-2xl font-bold text-blue-900">{totalRepos}</div>
            </div>
            <div className="bg-yellow-50 p-4 rounded-lg">
              <div className="text-sm text-yellow-600 font-medium">Á∑è„Çπ„Çø„ÉºÊï∞</div>
              <div className="text-2xl font-bold text-yellow-900">{totalStars.toLocaleString()}</div>
            </div>
            <div className="bg-green-50 p-4 rounded-lg">
              <div className="text-sm text-green-600 font-medium">Á∑è„Éï„Ç©„Éº„ÇØÊï∞</div>
              <div className="text-2xl font-bold text-green-900">{totalForks.toLocaleString()}</div>
            </div>
          </div>

          {/* Filters */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label htmlFor="search" className="block text-sm font-medium text-gray-700 mb-1">
                Ê§úÁ¥¢
              </label>
              <input
                id="search"
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="„É™„Éù„Ç∏„Éà„É™Âêç„Åæ„Åü„ÅØË™¨Êòé„ÅßÊ§úÁ¥¢..."
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              />
            </div>
