/* Search Page Specific Styles */

.search-header {
    background: linear-gradient(135deg, var(--primary), var(--accent));
    color: white;
    padding: calc(80px + var(--spacing-lg)) var(--spacing-md) var(--spacing-lg);
    text-align: center;
}

.search-header h1 {
    font-family: var(--font-display);
    font-size: 2.5rem;
    margin-bottom: var(--spacing-xs);
}

.search-header p {
    font-size: 1.1rem;
    opacity: 0.95;
}

.search-layout {
    padding: var(--spacing-lg) 0;
    background: var(--bg-secondary);
}

.search-container {
    display: grid;
    grid-template-columns: 280px 1fr;
    gap: var(--spacing-lg);
    align-items: start;
}

/* Filters Sidebar */
.filters-sidebar {
    background: white;
    border-radius: var(--radius-lg);
    padding: var(--spacing-md);
    box-shadow: var(--shadow-sm);
    position: sticky;
    top: 100px;
    max-height: calc(100vh - 120px);
    overflow-y: auto;
}

.filters-sidebar::-webkit-scrollbar {
    width: 6px;
}

.filters-sidebar::-webkit-scrollbar-track {
    background: var(--bg-secondary);
    border-radius: 3px;
}

.filters-sidebar::-webkit-scrollbar-thumb {
    background: var(--primary);
    border-radius: 3px;
}

.filter-section h3 {
    font-size: 1.25rem;
    margin-bottom: var(--spacing-md);
    color: var(--primary);
    font-family: var(--font-display);
}

.filter-group {
    margin-bottom: var(--spacing-md);
    padding-bottom: var(--spacing-md);
    border-bottom: 1px solid var(--border);
}

.filter-group:last-of-type {
    border-bottom: none;
}

.filter-group h4 {
    font-size: 0.875rem;
    font-weight: 700;
    margin-bottom: var(--spacing-sm);
    color: var(--text-primary);
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.checkbox-label {
    display: flex;
    align-items: center;
    gap: var(--spacing-xs);
    padding: 0.5rem 0;
    cursor: pointer;
    transition: var(--transition);
}

.checkbox-label:hover {
    color: var(--primary);
}

.checkbox-label input[type="checkbox"] {
    width: 18px;
    height: 18px;
    cursor: pointer;
    accent-color: var(--primary);
}

.checkbox-label .count {
    margin-left: auto;
    color: var(--text-secondary);
    font-size: 0.875rem;
}

.filter-select,
.filter-date {
    width: 100%;
    padding: 0.75rem;
    border: 1px solid var(--border);
    border-radius: var(--radius-sm);
    font-family: var(--font-body);
    font-size: 0.875rem;
    transition: var(--transition);
}

.filter-select:focus,
.filter-date:focus {
    outline: none;
    border-color: var(--primary);
    box-shadow: 0 0 0 3px var(--primary-light);
}

.price-inputs {
    display: flex;
    align-items: center;
    gap: var(--spacing-xs);
    margin-bottom: var(--spacing-sm);
}

.price-input {
    flex: 1;
    padding: 0.5rem;
    border: 1px solid var(--border);
    border-radius: var(--radius-sm);
    font-family: var(--font-body);
    font-size: 0.875rem;
}

.price-slider {
    width: 100%;
    accent-color: var(--primary);
}

.btn-full {
    width: 100%;
    margin-bottom: var(--spacing-sm);
}

.btn-reset {
    width: 100%;
    background: transparent;
    border: none;
    color: var(--text-secondary);
    padding: 0.5rem;
    cursor: pointer;
    font-family: var(--font-body);
    font-size: 0.875rem;
    transition: var(--transition);
}

.btn-reset:hover {
    color: var(--primary);
}

/* Results Area */
.results-area {
    background: transparent;
}

.results-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: var(--spacing-md);
    flex-wrap: wrap;
    gap: var(--spacing-sm);
}

.results-info h2 {
    font-size: 1.5rem;
    color: var(--text-primary);
}

.results-controls {
    display: flex;
    gap: var(--spacing-sm);
    align-items: center;
}

.sort-select {
    padding: 0.5rem 1rem;
    border: 1px solid var(--border);
    border-radius: var(--radius-sm);
    background: white;
    font-family: var(--font-body);
    cursor: pointer;
    transition: var(--transition);
}

.sort-select:focus {
    outline: none;
    border-color: var(--primary);
}

.view-toggle {
    display: flex;
    gap: 0.25rem;
    background: white;
    padding: 0.25rem;
    border-radius: var(--radius-sm);
    box-shadow: var(--shadow-sm);
}

.view-btn {
    padding: 0.5rem;
    background: transparent;
    border: none;
    cursor: pointer;
    border-radius: var(--radius-sm);
    color: var(--text-secondary);
    transition: var(--transition);
}

.view-btn:hover {
    background: var(--bg-secondary);
}

.view-btn.active {
    background: var(--primary);
    color: white;
}

/* Active Filters */
.active-filters {
    display: flex;
    gap: var(--spacing-xs);
    margin-bottom: var(--spacing-md);
    flex-wrap: wrap;
}

.filter-tag {
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
    background: var(--primary-light);
    color: var(--primary);
    padding: 0.5rem 1rem;
    border-radius: var(--radius-full);
    font-size: 0.875rem;
    font-weight: 600;
}

.remove-filter {
    background: transparent;
    border: none;
    color: var(--primary);
    font-size: 1.25rem;
    cursor: pointer;
    padding: 0;
    width: 20px;
    height: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: var(--transition);
}

.remove-filter:hover {
    transform: scale(1.2);
}

/* Musicians Results */
.musicians-results {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(320px, 1fr));
    gap: var(--spacing-md);
    margin-bottom: var(--spacing-lg);
}

.musician-card-search {
    background: white;
    border-radius: var(--radius-lg);
    overflow: hidden;
    box-shadow: var(--shadow-sm);
    transition: var(--transition);
    display: flex;
    flex-direction: column;
}

.musician-card-search:hover {
    transform: translateY(-4px);
    box-shadow: var(--shadow-lg);
}

.musician-image-search {
    position: relative;
    width: 100%;
    height: 240px;
    overflow: hidden;
}

.musician-image-search img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: var(--transition);
}

.musician-card-search:hover .musician-image-search img {
    transform: scale(1.05);
}

.favorite-btn {
    position: absolute;
    top: var(--spacing-sm);
    left: var(--spacing-sm);
    width: 40px;
    height: 40px;
    background: rgba(255, 255, 255, 0.9);
    border: none;
    border-radius: 50%;
    font-size: 1.5rem;
