}

.musician-bio-search {
    font-size: 0.875rem;
    color: var(--text-secondary);
    line-height: 1.6;
    margin-bottom: var(--spacing-sm);
    flex: 1;
}

.musician-footer {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: auto;
}

.price-search {
    font-weight: 700;
    color: var(--primary);
    font-size: 1.1rem;
}

.btn-small {
    padding: 0.5rem 1rem;
    font-size: 0.875rem;
}

/* Pagination */
.pagination {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 0.5rem;
    margin-top: var(--spacing-lg);
}

.page-btn {
    padding: 0.5rem 1rem;
    background: white;
    border: 1px solid var(--border);
    border-radius: var(--radius-sm);
    cursor: pointer;
    transition: var(--transition);
    font-family: var(--font-body);
    font-weight: 500;
}

.page-btn:hover:not(:disabled) {
    border-color: var(--primary);
    color: var(--primary);
}

.page-btn.active {
    background: var(--primary);
    color: white;
    border-color: var(--primary);
}

.page-btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
}

.page-dots {
    color: var(--text-secondary);
}

.nav-link.active {
    color: var(--primary);
    font-weight: 700;
}

/* Responsive */
@media (max-width: 1024px) {
    .search-container {
        grid-template-columns: 1fr;
    }

    .filters-sidebar {
        position: static;
        max-height: none;
    }
}

@media (max-width: 768px) {
    .search-header h1 {
        font-size: 2rem;
    }

    .results-header {
        flex-direction: column;
        align-items: flex-start;
    }

    .musicians-results {
        grid-template-columns: 1fr;
    }

    .results-controls {
        width: 100%;
        justify-content: space-between;
    }
}

