// Update active filter tags
function updateActiveFilters() {
    const activeFiltersContainer = document.querySelector('.active-filters');
    if (!activeFiltersContainer) return;

    // Clear existing tags except the default one
    const existingTags = activeFiltersContainer.querySelectorAll('.filter-tag');
    existingTags.forEach((tag, index) => {
        if (index > 0) tag.remove();
    });

    // Add instrument tags
    filters.instruments.forEach(instrument => {
        const tag = createFilterTag(getInstrumentName(instrument));
        activeFiltersContainer.appendChild(tag);
    });

    // Add skill tags
    filters.skills.forEach(skill => {
        const tag = createFilterTag(getSkillName(skill));
        activeFiltersContainer.appendChild(tag);
    });

    // Add area tag
    if (filters.area) {
        const tag = createFilterTag(filters.area);
        activeFiltersContainer.appendChild(tag);
    }

    // Add verified tag
    if (filters.verified) {
        const tag = createFilterTag('Ë™çË®ºÊ∏à„Åø');
        activeFiltersContainer.appendChild(tag);
    }
}

// Create filter tag element
function createFilterTag(text) {
    const tag = document.createElement('span');
    tag.className = 'filter-tag';
    tag.innerHTML = `
        ${text}
        <button class="remove-filter">√ó</button>
    `;

    tag.querySelector('.remove-filter').addEventListener('click', () => {
        tag.remove();
        // TODO: Update corresponding filter
    });

    return tag;
}

