{
  "name": "metaprojects",
  "version": "1.0.0",
  "type": "module",
  "description": "GitHub Repositories Dashboard",
  "main": "index.js",
  "scripts": {
    "dev": "vite",
    "build": "tsc && vite build",
    "preview": "vite preview",
    "fetch-repos": "node scripts/fetch-repos.js"
  },
  "repository": {
    "type": "git",
    "url": "git+https://github.com/maskin/metaprojects.git"
  },
  "keywords": [],
  "author": "",
  "license": "ISC",
  "bugs": {
    "url": "https://github.com/maskin/metaprojects/issues"
  },
  "homepage": "https://maskin.github.io/metaprojects/",
  "dependencies": {
    "@types/react-syntax-highlighter": "^15.5.13",
    "react": "^19.2.0",
    "react-dom": "^19.2.0",
    "react-syntax-highlighter": "^16.1.0"
  },
  "devDependencies": {
    "@types/react": "^19.2.5",
    "@types/react-dom": "^19.2.3",
    "@vitejs/plugin-react": "^5.1.1",
    "autoprefixer": "^10.4.22",
    "postcss": "^8.5.6",
    "tailwindcss": "^3.4.18",
    "typescript": "^5.9.3",
    "vite": "^7.2.2"
  }
}

@tailwind base;
@tailwind components;
@tailwind utilities;

body {
  margin: 0;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen',
    'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue',
    sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

code {
  font-family: source-code-pro, Menlo, Monaco, Consolas, 'Courier New',
    monospace;
}

import React, { useState, useMemo, useEffect } from 'react';
import RepoCard from './components/RepoCard';
import RepoDetail from './components/RepoDetail';
import { RepoData } from './types';

const App: React.FC = () => {
  const [selectedRepo, setSelectedRepo] = useState<RepoData | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [languageFilter, setLanguageFilter] = useState<string>('all');
  const [sortBy, setSortBy] = useState<'updated' | 'stars' | 'name'>('updated');
  const [repos, setRepos] = useState<RepoData[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const basePath = import.meta.env.BASE_URL || '/';
    fetch(`${basePath}repos-data.json`)
      .then(res => res.json())
      .then((data: RepoData[]) => {
        setRepos(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Failed to load repository data:', err);
        setLoading(false);
      });
  }, []);

  // Âà©Áî®ÂèØËÉΩ„Å™Ë®ÄË™û„ÅÆ„É™„Çπ„Éà„ÇíÂèñÂæó
  const availableLanguages = useMemo(() => {
    const languages = new Set<string>();
    repos.forEach(repo => {
      if (repo.language) {
        languages.add(repo.language);
      }
    });
    return Array.from(languages).sort();
  }, [repos]);

  // „Éï„Ç£„É´„Çø„É™„É≥„Ç∞„Å®„ÇΩ„Éº„Éà
  const filteredAndSortedRepos = useMemo(() => {
    let filtered = repos.filter(repo => {
      const matchesSearch = repo.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (repo.description && repo.description.toLowerCase().includes(searchQuery.toLowerCase()));
      const matchesLanguage = languageFilter === 'all' || repo.language === languageFilter;
      return matchesSearch && matchesLanguage;
    });

    // „ÇΩ„Éº„Éà
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'stars':
          return b.stars - a.stars;
        case 'name':
          return a.name.localeCompare(b.name);
        case 'updated':
        default:
          return new Date(b.updatedAt).getTime() - new Date(a.updatedAt).getTime();
      }
    });

    return filtered;
  }, [repos, searchQuery, languageFilter, sortBy]);

  const totalRepos = repos.length;
  const totalStars = repos.reduce((sum, repo) => sum + repo.stars, 0);
  const totalForks = repos.reduce((sum, repo) => sum + repo.forks, 0);

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">„Éá„Éº„Çø„ÇíË™≠„ÅøËæº„Çì„Åß„ÅÑ„Åæ„Åô...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">
            GitHub Repositories Dashboard
          </h1>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
            <div className="bg-blue-50 p-4 rounded-lg">
              <div className="text-sm text-blue-600 font-medium">Á∑è„É™„Éù„Ç∏„Éà„É™Êï∞</div>
              <div className="text-2xl font-bold text-blue-900">{totalRepos}</div>
            </div>
            <div className="bg-yellow-50 p-4 rounded-lg">
              <div className="text-sm text-yellow-600 font-medium">Á∑è„Çπ„Çø„ÉºÊï∞</div>
              <div className="text-2xl font-bold text-yellow-900">{totalStars.toLocaleString()}</div>
            </div>
            <div className="bg-green-50 p-4 rounded-lg">
              <div className="text-sm text-green-600 font-medium">Á∑è„Éï„Ç©„Éº„ÇØÊï∞</div>
              <div className="text-2xl font-bold text-green-900">{totalForks.toLocaleString()}</div>
            </div>
          </div>

          {/* Filters */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label htmlFor="search" className="block text-sm font-medium text-gray-700 mb-1">
                Ê§úÁ¥¢
              </label>
              <input
                id="search"
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="„É™„Éù„Ç∏„Éà„É™Âêç„Åæ„Åü„ÅØË™¨Êòé„ÅßÊ§úÁ¥¢..."
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              />
            </div>
            <div>
              <label htmlFor="language" className="block text-sm font-medium text-gray-700 mb-1">
                Ë®ÄË™û
              </label>
              <select
                id="language"
                value={languageFilter}
                onChange={(e) => setLanguageFilter(e.target.value)}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="all">„Åô„Åπ„Å¶</option>
                {availableLanguages.map(lang => (
                  <option key={lang} value={lang}>{lang}</option>
                ))}
              </select>
            </div>
            <div>
              <label htmlFor="sort" className="block text-sm font-medium text-gray-700 mb-1">
                ‰∏¶„Å≥Êõø„Åà
              </label>
              <select
                id="sort"
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value as 'updated' | 'stars' | 'name')}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="updated">ÊúÄÁµÇÊõ¥Êñ∞Êó•</option>
                <option value="stars">„Çπ„Çø„ÉºÊï∞</option>
                <option value="name">ÂêçÂâç</option>
              </select>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-4 text-gray-600">
          {filteredAndSortedRepos.length} ‰ª∂„ÅÆ„É™„Éù„Ç∏„Éà„É™„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åó„Åü
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredAndSortedRepos.map((repo) => (
            <RepoCard
              key={repo.id}
              repo={repo}
              onClick={() => setSelectedRepo(repo)}
            />
          ))}
        </div>
        {filteredAndSortedRepos.length === 0 && (
          <div className="text-center py-12">
            <p className="text-gray-500 text-lg">„É™„Éù„Ç∏„Éà„É™„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åõ„Çì„Åß„Åó„Åü</p>
          </div>
        )}
      </main>

      {/* Detail Modal */}
      {selectedRepo && (
        <RepoDetail
          repo={selectedRepo}
          onClose={() => setSelectedRepo(null)}
        />
      )}
    </div>
  );
};

export default App;

export default {
  plugins: {
    tailwindcss: {},
    autoprefixer: {},
  },
}

// 3DËø∑Ë∑Ø„Ç≤„Éº„É† - „É°„Ç§„É≥Âà∂Âæ°„Ç∑„Çπ„ÉÜ„É†
if (typeof window.GameManager === 'undefined') {
class GameManager {
    constructor() {
        this.gameEngine = null;
        this.mazeGenerator = null;
        this.controls = null;
        this.uiManager = null;
        this.isInitialized = false;
    }

    async init() {
        console.log('üéÆ „Ç≤„Éº„É†ÁÆ°ÁêÜ„Ç∑„Çπ„ÉÜ„É†ÂàùÊúüÂåñÈñãÂßã...');
        console.log('Environment check:', {
            window: typeof window,
            document: typeof document,
            THREE: typeof THREE,
            GameEngine: typeof GameEngine,
            MazeGenerator: typeof MazeGenerator,
            Controls: typeof Controls,
            UIManager: typeof UIManager
        });

        try {
            // Step 1: „Ç®„É©„Éº„Éè„É≥„Éâ„É™„É≥„Ç∞Ë®≠ÂÆö
            console.log('üìã ÂàùÊúüÂåñ Step 1: „Ç®„É©„Éº„Éè„É≥„Éâ„É™„É≥„Ç∞Ë®≠ÂÆö...');
            this.setupErrorHandling();
            console.log('‚úÖ Step 1 ÂÆå‰∫Ü: „Ç®„É©„Éº„Éè„É≥„Éâ„É™„É≥„Ç∞Ë®≠ÂÆöÊ∏à„Åø');

            // Step 2: UI„Éû„Éç„Éº„Ç∏„É£„ÉºÂàùÊúüÂåñ
            console.log('üìã ÂàùÊúüÂåñ Step 2: UIManagerÂàùÊúüÂåñ...');
            this.uiManager = new UIManager();
            window.uiManager = this.uiManager;
            console.log('‚úÖ Step 2 ÂÆå‰∫Ü: UIManagerÂàùÊúüÂåñÊ∏à„Åø');

            // Step 3: 3D„Ç®„É≥„Ç∏„É≥ÂàùÊúüÂåñ
            console.log('üìã ÂàùÊúüÂåñ Step 3: GameEngineÂàùÊúüÂåñ...');
            this.gameEngine = new GameEngine();
            console.log('GameEngine object created, calling init...');

            const engineInit = await this.gameEngine.init();
            console.log('GameEngine init result:', engineInit);

            if (!engineInit) {
                console.warn('‚ö†Ô∏è GameEngineÂàùÊúüÂåñÂ§±Êïó„ÄÅÂÜçË©¶Ë°å„Åó„Åæ„Åô...');
                // Wait a bit and try again
                await new Promise(resolve => setTimeout(resolve, 1000));
                const retryInit = await this.gameEngine.init();
                console.log('GameEngine retry init result:', retryInit);

                if (!retryInit) {
                    throw new Error('3D„Ç®„É≥„Ç∏„É≥„ÅÆÂàùÊúüÂåñ„Å´Â§±Êïó„Åó„Åæ„Åó„Åü (ÂÜçË©¶Ë°åÂæå„ÇÇÂ§±Êïó)');
                }
            }
            console.log('‚úÖ Step 3 ÂÆå‰∫Ü: GameEngineÂàùÊúüÂåñÊ∏à„Åø');

            // Step 4: Ëø∑Ë∑ØÁîüÊàêÂô®ÂàùÊúüÂåñ
            console.log('üìã ÂàùÊúüÂåñ Step 4: MazeGeneratorÂàùÊúüÂåñ...');
            this.mazeGenerator = new MazeGenerator(50, 50);
            console.log('‚úÖ Step 4 ÂÆå‰∫Ü: MazeGeneratorÂàùÊúüÂåñÊ∏à„Åø');

            // Step 5: Êìç‰Ωú„Ç∑„Çπ„ÉÜ„É†ÂàùÊúüÂåñ
            console.log('üìã ÂàùÊúüÂåñ Step 5: ControlsÂàùÊúüÂåñ...');
            this.controls = new Controls(this.gameEngine);
            console.log('‚úÖ Step 5 ÂÆå‰∫Ü: ControlsÂàùÊúüÂåñÊ∏à„Åø');

            // Step 6: PWAË®≠ÂÆö
            console.log('üìã ÂàùÊúüÂåñ Step 6: PWAË®≠ÂÆö...');
            this.initPWA();
            console.log('‚úÖ Step 6 ÂÆå‰∫Ü: PWAË®≠ÂÆöÊ∏à„Åø');

            // Step 7: „Ç¢„Éã„É°„Éº„Ç∑„Éß„É≥ÈñãÂßã
            console.log('üìã ÂàùÊúüÂåñ Step 7: „Ç¢„Éã„É°„Éº„Ç∑„Éß„É≥ÈñãÂßã...');
            console.log('üöÄ GameEngine.animate()Èñ¢Êï∞Â≠òÂú®Á¢∫Ë™ç:', typeof this.gameEngine.animate);
            console.log('üöÄ GameEngine.animate()Èñ¢Êï∞Âëº„Å≥Âá∫„ÅóÁõ¥Ââç');
            this.gameEngine.animate();
            console.log('üöÄ GameEngine.animate()Èñ¢Êï∞Âëº„Å≥Âá∫„ÅóÂÆå‰∫Ü');
            console.log('‚úÖ Step 7 ÂÆå‰∫Ü: „Ç¢„Éã„É°„Éº„Ç∑„Éß„É≥ÈñãÂßãÊ∏à„Åø');

            // ÂàùÊúüÂåñÂÆå‰∫Ü„Éï„É©„Ç∞Ë®≠ÂÆö
            this.isInitialized = true;
            console.log('üéâ „Ç≤„Éº„É†ÁÆ°ÁêÜ„Ç∑„Çπ„ÉÜ„É†ÂàùÊúüÂåñÂÆå‰∫ÜÔºÅ');

            // Step 8: „Çπ„Çø„Éº„Éà„É°„Éã„É•„ÉºË°®Á§∫
            console.log('üìã ÂàùÊúüÂåñ Step 8: „Çπ„Çø„Éº„Éà„É°„Éã„É•„ÉºË°®Á§∫...');
            this.uiManager.showStartMenu();
            console.log('‚úÖ Step 8 ÂÆå‰∫Ü: „Çπ„Çø„Éº„Éà„É°„Éã„É•„ÉºË°®Á§∫Ê∏à„Åø');

            console.log('üéÆ „Åô„Åπ„Å¶„ÅÆÂàùÊúüÂåñ„Éó„É≠„Çª„ÇπÂÆå‰∫Ü - „Ç≤„Éº„É†Ê∫ñÂÇôÂÆå‰∫ÜÔºÅ');

        } catch (error) {
            console.error('‚ùå ÂàùÊúüÂåñ„Ç®„É©„Éº - Ë©≥Á¥∞ÊÉÖÂ†±:', {
                error: error,
                message: error.message,
                stack: error.stack,
                engineState: this.gameEngine ? 'created' : 'null',
                uiManagerState: this.uiManager ? 'created' : 'null'
            });

            // Á∑äÊÄ•ÊôÇÁî®„ÅÆÈÉ®ÂàÜÂàùÊúüÂåñ„ÇíË©¶Ë°å
            console.log('üö® Á∑äÊÄ•ÊôÇÁî®„ÅÆÈÉ®ÂàÜÂàùÊúüÂåñ„ÇíË©¶Ë°å...');
            try {
                await this.emergencyInit();
            } catch (emergencyError) {
                console.error('‚ùå Á∑äÊÄ•ÂàùÊúüÂåñ„ÇÇÂ§±Êïó:', emergencyError);

                // Make sure we have UIManager before showing error
                if (this.uiManager) {
                    this.uiManager.showError('„Ç≤„Éº„É†„ÅÆÂàùÊúüÂåñ„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:\n' + error.message + '\n\nÁ∑äÊÄ•„É¢„Éº„Éâ„ÇÇÂà©Áî®„Åß„Åç„Åæ„Åõ„Çì„ÄÇ');
                } else {
                    // Fallback error display
                    alert('„Ç≤„Éº„É†„ÅÆÂàùÊúüÂåñ„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:\n' + error.message);
                }
            }
        }
    }

    async emergencyInit() {
        console.log('üö® Á∑äÊÄ•ÂàùÊúüÂåñ„É¢„Éº„ÉâÈñãÂßã...');

        // ÊúÄ‰ΩéÈôê„ÅÆUIManagerÁ¢∫‰øù
        if (!this.uiManager) {
            console.log('üîß UIManagerÁ∑äÊÄ•‰ΩúÊàê...');
            this.uiManager = new UIManager();
            window.uiManager = this.uiManager;
        }

        // ÊúÄ‰ΩéÈôê„ÅÆ„Ç≤„Éº„É†„Ç®„É≥„Ç∏„É≥Á¢∫‰øù
        if (!this.gameEngine || !this.gameEngine.renderer) {
            console.log('üîß Á∞°Êòì„Ç≤„Éº„É†„Ç®„É≥„Ç∏„É≥‰ΩúÊàêË©¶Ë°å...');
            try {
                this.gameEngine = new GameEngine();
                // Á∞°ÊòìÂàùÊúüÂåñ„ÇíË©¶Ë°å
                const simpleInit = await this.gameEngine.simpleInit();
                if (!simpleInit) {
                    throw new Error('Á∞°ÊòìÂàùÊúüÂåñÂ§±Êïó');
                }
            } catch (e) {
                console.warn('‚ö†Ô∏è 3D„Ç®„É≥„Ç∏„É≥ÂàùÊúüÂåñÂ§±Êïó„ÄÅ2D„Éï„Ç©„Éº„É´„Éê„ÉÉ„ÇØ„É¢„Éº„Éâ');
                this.gameEngine = null;
            }
        }

        // Âü∫Êú¨ÁöÑ„Å™„Ç≥„É≥„Éù„Éº„Éç„É≥„Éà
        if (!this.mazeGenerator) {
            this.mazeGenerator = new MazeGenerator(25, 25); // „Çà„ÇäÂ∞è„Åï„Å™„Çµ„Ç§„Ç∫
        }

        // ÈÉ®ÂàÜÁöÑ„Å´ÂàùÊúüÂåñÂÆå‰∫Ü
        this.isInitialized = true;
        console.log('üÜò Á∑äÊÄ•ÂàùÊúüÂåñÂÆå‰∫Ü - Âà∂Èôê‰ªò„Åç„É¢„Éº„Éâ');

        // „Ç®„É©„Éº„É°„ÉÉ„Çª„Éº„Ç∏„Å®ÂÜçË©¶Ë°å„Ç™„Éó„Ç∑„Éß„É≥Ë°®Á§∫
        this.uiManager.showError('ÂàùÊúüÂåñ„ÅßÂïèÈ°å„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü„Åå„ÄÅÂà∂Èôê‰ªò„Åç„É¢„Éº„Éâ„ÅßÂãï‰Ωú„Åó„Åæ„Åô„ÄÇ\n\n„Éö„Éº„Ç∏„ÇíÂÜçË™≠„ÅøËæº„Åø„Åó„Å¶ÂÆåÂÖ®ÂàùÊúüÂåñ„ÇíÂÜçË©¶Ë°å„Åô„Çã„Åì„Å®„Çí„ÅäÂãß„ÇÅ„Åó„Åæ„Åô„ÄÇ', {
            showReload: true,
            showContinue: true
        });
    }

    setupErrorHandling() {
        // „Ç∞„É≠„Éº„Éê„É´„Ç®„É©„Éº„Éè„É≥„Éâ„É™„É≥„Ç∞
        window.addEventListener('error', (event) => {
            console.error('JavaScript „Ç®„É©„Éº:', event.error);
            if (this.uiManager) {
                this.uiManager.showError('„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü:\n' + event.error.message);
            }
        });

        // Promise „ÅÆ„Ç®„É©„Éº„Éè„É≥„Éâ„É™„É≥„Ç∞
        window.addEventListener('unhandledrejection', (event) => {
            console.error('Êú™Âá¶ÁêÜ„ÅÆPromise„Ç®„É©„Éº:', event.reason);
            if (this.uiManager) {
                this.uiManager.showError('Âá¶ÁêÜ„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü:\n' + event.reason);
            }
        });
    }

    async startGame() {
        if (!this.isInitialized) {
            console.log('„Ç≤„Éº„É†„ÅåÂàùÊúüÂåñ„Åï„Çå„Å¶„ÅÑ„Åæ„Åõ„Çì - ÂÜçÂàùÊúüÂåñ„ÇíË©¶Ë°å...');
            // Try to re-initialize
            try {
                await this.init();
                if (!this.isInitialized) {
