// Musician card click
document.querySelectorAll('.musician-card').forEach(card => {
    card.addEventListener('click', () => {
        alert('å¥è€…è©³ç´°ãƒšãƒ¼ã‚¸ã¸é·ç§»ï¼ˆå®Ÿè£…äºˆå®šï¼‰\n\nãƒ—ãƒ­ãƒ•ã‚£ãƒ¼ãƒ«è©³ç´°ã€ã‚«ãƒ¬ãƒ³ãƒ€ãƒ¼ã€ãƒ¬ãƒ“ãƒ¥ãƒ¼ãªã©ãŒè¡¨ç¤ºã•ã‚Œã¾ã™ã€‚');
