// Search functionality
const searchButton = document.querySelector('.btn-search');
if (searchButton) {
    searchButton.addEventListener('click', () => {
        const instrument = document.querySelector('.search-field select:nth-of-type(1)').value;
        const area = document.querySelector('.search-field select:nth-of-type(2)').value;
