// Favorite button functionality
document.querySelectorAll('.favorite-btn').forEach(btn => {
    btn.addEventListener('click', (e) => {
        e.stopPropagation();
        btn.classList.toggle('active');
        if (btn.classList.contains('active')) {
            btn.textContent = '♥';
            console.log('Added to favorites');
        } else {
            btn.textContent = '♡';
            console.log('Removed from favorites');
        }
    });
});

// View toggle functionality
document.querySelectorAll('.view-btn').forEach(btn => {
    btn.addEventListener('click', () => {
        document.querySelectorAll('.view-btn').forEach(b => b.classList.remove('active'));
        btn.classList.add('active');

        const view = btn.dataset.view;
        const resultsContainer = document.querySelector('.musicians-results');

        if (view === 'list') {
            resultsContainer.style.gridTemplateColumns = '1fr';
        } else {
            resultsContainer.style.gridTemplateColumns = 'repeat(auto-fill, minmax(320px, 1fr))';
        }
    });
});

// Filter functionality
const filters = {
    instruments: [],
    area: '',
    skills: [],
    priceMin: 0,
    priceMax: 50000,
    ratings: [],
    date: '',
    verified: false
};

// Instrument filter
document.querySelectorAll('input[name="instrument"]').forEach(checkbox => {
    checkbox.addEventListener('change', (e) => {
        if (e.target.checked) {
            filters.instruments.push(e.target.value);
        } else {
            filters.instruments = filters.instruments.filter(i => i !== e.target.value);
        }
        updateResults();
    });
});

// Skill level filter
document.querySelectorAll('input[name="skill"]').forEach(checkbox => {
    checkbox.addEventListener('change', (e) => {
        if (e.target.checked) {
            filters.skills.push(e.target.value);
        } else {
            filters.skills = filters.skills.filter(s => s !== e.target.value);
        }
        updateResults();
    });
});

// Rating filter
document.querySelectorAll('input[name="rating"]').forEach(checkbox => {
    checkbox.addEventListener('change', (e) => {
        if (e.target.checked) {
            filters.ratings.push(e.target.value);
        } else {
            filters.ratings = filters.ratings.filter(r => r !== e.target.value);
        }
        updateResults();
    });
});

// Verified filter
const verifiedCheckbox = document.querySelector('input[name="verified"]');
if (verifiedCheckbox) {
    verifiedCheckbox.addEventListener('change', (e) => {
        filters.verified = e.target.checked;
        updateResults();
    });
}

// Price range
const priceInputs = document.querySelectorAll('.price-input');
const priceSlider = document.querySelector('.price-slider');

priceInputs.forEach(input => {
    input.addEventListener('change', (e) => {
        if (input.placeholder === '最低') {
            filters.priceMin = parseInt(e.target.value) || 0;
        } else {
            filters.priceMax = parseInt(e.target.value) || 50000;
        }
        updateResults();
    });
});

if (priceSlider) {
    priceSlider.addEventListener('input', (e) => {
        filters.priceMax = parseInt(e.target.value);
        priceInputs[1].value = filters.priceMax;
    });
}

// Area filter
const areaSelect = document.querySelector('.filter-select');
if (areaSelect) {
    areaSelect.addEventListener('change', (e) => {
        filters.area = e.target.value;
        updateResults();
    });
}

// Date filter
const dateInput = document.querySelector('.filter-date');
if (dateInput) {
    dateInput.addEventListener('change', (e) => {
        filters.date = e.target.value;
        updateResults();
    });
}

// Sort functionality
const sortSelect = document.querySelector('.sort-select');
if (sortSelect) {
    sortSelect.addEventListener('change', (e) => {
        const sortBy = e.target.value;
        console.log('Sorting by:', sortBy);
        // TODO: Implement actual sorting logic
        updateResults();
    });
}

// Remove filter tag
document.querySelectorAll('.remove-filter').forEach(btn => {
    btn.addEventListener('click', (e) => {
        e.target.closest('.filter-tag').remove();
        // TODO: Update corresponding checkbox
        updateResults();
    });
});

// Reset filters
const resetBtn = document.querySelector('.btn-reset');
if (resetBtn) {
    resetBtn.addEventListener('click', () => {
        // Reset all checkboxes
        document.querySelectorAll('input[type="checkbox"]').forEach(cb => cb.checked = false);

        // Reset selects
        document.querySelectorAll('select').forEach(select => select.selectedIndex = 0);

        // Reset price inputs
        priceInputs[0].value = 0;
        priceInputs[1].value = 50000;

        // Reset date
        if (dateInput) dateInput.value = '';

        // Reset filters object
        filters.instruments = [];
        filters.area = '';
        filters.skills = [];
        filters.priceMin = 0;
        filters.priceMax = 50000;
        filters.ratings = [];
        filters.date = '';
        filters.verified = false;

        // Remove active filter tags
        document.querySelectorAll('.filter-tag').forEach(tag => tag.remove());

        updateResults();
    });
}

// Update results based on filters
function updateResults() {
    console.log('Current filters:', filters);

    // TODO: Make API call with filters
    // For now, just update the count
    const resultsInfo = document.querySelector('.results-info h2');
    if (resultsInfo) {
        // Simulate filtered count
        const count = Math.floor(Math.random() * 200) + 50;
        resultsInfo.textContent = `${count}件の奏者が見つかりました`;
    }

    updateActiveFilters();
}

// Update active filter tags
function updateActiveFilters() {
    const activeFiltersContainer = document.querySelector('.active-filters');
    if (!activeFiltersContainer) return;

    // Clear existing tags except the default one
    const existingTags = activeFiltersContainer.querySelectorAll('.filter-tag');
    existingTags.forEach((tag, index) => {
        if (index > 0) tag.remove();
    });

    // Add instrument tags
    filters.instruments.forEach(instrument => {
        const tag = createFilterTag(getInstrumentName(instrument));
        activeFiltersContainer.appendChild(tag);
    });

    // Add skill tags
    filters.skills.forEach(skill => {
        const tag = createFilterTag(getSkillName(skill));
        activeFiltersContainer.appendChild(tag);
    });

    // Add area tag
    if (filters.area) {
        const tag = createFilterTag(filters.area);
        activeFiltersContainer.appendChild(tag);
    }

    // Add verified tag
    if (filters.verified) {
        const tag = createFilterTag('認証済み');
        activeFiltersContainer.appendChild(tag);
    }
}

// Create filter tag element
function createFilterTag(text) {
    const tag = document.createElement('span');
    tag.className = 'filter-tag';
    tag.innerHTML = `
        ${text}
        <button class="remove-filter">×</button>
    `;

    tag.querySelector('.remove-filter').addEventListener('click', () => {
        tag.remove();
        // TODO: Update corresponding filter
    });

    return tag;
}

// Helper functions
function getInstrumentName(value) {
    const names = {
        'violin': 'バイオリン',
        'cello': 'チェロ',
        'viola': 'ビオラ',
        'harp': 'ハープ',
        'piano': 'ピアノ'
    };
    return names[value] || value;
}

function getSkillName(value) {
    const names = {
        'professional': 'プロフェッショナル',
        'semi-pro': 'セミプロ',
        'amateur': 'アマチュア'
    };
    return names[value] || value;
}

